		s2.add(i,X[i]*2LL+bef);
		int pos=lower_bound(X,X+n,X[i]+T/2+1)-X-1;
		dp[i]=s1.get(i,pos+1)-X[i]+(ll) T;
		if(pos+1<n) dp[i]=min(dp[i],s2.get(pos+1,n)-(ll) X[i]*3LL);
		bef=X[i]+dp[i];
	}
	printf("%lld\n",bef);
	return 0;
}

