    LL sum = 0;
    sum += num[0] + e - num[n - 1];
    //cout << sum << endl;
    for(int j, i = 0; i < n; i = j)
    {
        j = i;
        while(j < n && num[j] - num[i] <  t) j++;
        sum += 3LL * (num[j - 1] - num[i]);
        for(int k = i; k < j; k++)
        {
            if((num[j - 1] - num[i]) * 2 < t) sum += (t - num[j - 1] + num[i]);
        }
        if(j < n) sum += num[j] - num[j - 1];
        //cout << i << " " << j << endl;
    }
    cout << sum << endl;
    return 0;
}

