	if (r <= s || e <= l)
		return 1e17;
	if (l <= s && e <= r)
		return seg[v];
	seg[v * 2] += laz[v];
	seg[v * 2 + 1] += laz[v];
	laz[v] = 0;
	return min(get(l, r, v * 2, s, mid), get(l, r, v * 2 + 1, mid, e));
