		return;
	if (l <= s && e <= r) {
		seg[v] += val;
		laz[v] += val;
		return;
	}
	seg[v * 2] += laz[v];
	seg[v * 2 + 1] += laz[v];
	laz[v * 2] += laz[v];
	laz[v * 2 + 1] += laz[v];
	laz[v] = 0;
	add(l, r, val, v * 2, s, mid);
	add(l, r, val, v * 2 + 1, mid, e);
	seg[v] = min(seg[v * 2], seg[v * 2 + 1]);
}

