using namespace std;
typedef long long ll;

struct segtree {
       vector<ll> tree;

       ll op(ll x, ll y) {
               return min(x,y);
       }
       ll const INIT_VALUE = 1e18;
       ll n_ = 1;

       segtree(ll n) {
               while (n_ <= n) {
                       n_ *= 2;
               }
               tree = vector<ll>(n_ * 2 + 1, INIT_VALUE);
       }

       //kç•ªç›®ã‚’xã§update
       void update(ll k, ll x) {
               k += n_;
               tree[k] = x;
               while (k >= 1) {
                       k /= 2;
                       tree[k] = op(tree[k * 2], tree[k * 2 + 1]);
               }
       }

       ll val_o(ll a, ll b, ll k, ll l, ll r) {
               if (a <= l && r <= b) {
                       return tree[k];
               }
               else if (r <= a || b <= l) {
                       return INIT_VALUE;
               }
               else {
                       ll x1 = val_o(a, b, k * 2, l, (l + r) / 2);
                       ll x2 = val_o(a, b, k * 2 + 1, (l + r) / 2, r);
                       return op(x1, x2);
               }

       }

       //[a,b)ã®valueã‚’æ±‚ã‚ã‚‹
        ll val(ll a, ll b) {
            if(a >= b){
                return INIT_VALUE;
            }
            return val_o(a, b, 1, 0, n_);
        }
};

ll const MAX = 1e5 + 5;
segtree tree1(MAX), tree2(MAX);
vll a(MAX);
vll dp(MAX);
int main(){
    ll n,e,t;
    cin >> n >> e >> t;
    a[0] = 0;
    REP(i,1,n){
        cin >> a[i];
    }
    tree1.update(0,0);
    tree2.update(0,-2 * a[1]);
    a.push_back(1e18);
    REP(i,1,n){
        ll tmp = lower_bound(a.begin(), a.begin() + n + 1, a[i] - double(t) / 2) - 1 - a.begin();
        ll k1,k2;
        k1 = tree1.val(tmp, i) + a[i] + t;
        k2 = tree2.val(0, tmp) + 3 * a[i];
        dp[i] = min(k1,k2);
        tree1.update(i, dp[i] - a[i]);
        tree2.update(i, dp[i] - a[i] - 2*a[i+1]);
    }
    cout << dp[n] + e - a[n]<< endl;    
}

#include<bits/stdc++.h>
using namespace std;

typedef long long ll;

const int MN = 100010;

int N, E, T;
int X[MN];

struct BIT {
    vector<ll> tree;
    void init() {
        tree = vector<ll>(4 * MN, 1e18);
    }
    void upd(int idx, ll val, int l, int r, int n) {
        if(idx < l || r < idx) return;
        if(l == r) {
            tree[n] = min(tree[n], val);
            return;
        }
        int m = (l + r)>>1;
        upd(idx, val, l, m, 2*n);
        upd(idx, val, m + 1, r, 2*n + 1);
        tree[n] = min(tree[2*n], tree[2*n + 1]);
    }
    ll quer(int a, int b, int l, int r, int n) {
        if(b < l || r < a) return 1e18;
        if(a <= l && r <= b) return tree[n];
        int m = (l + r)>>1;
        ll L = quer(a, b, l, m, 2*n);
        ll R = quer(a, b, m + 1, r, 2*n + 1);
        return min(L, R);
    }
} bit1, bit2;

ll dp[MN];

int main() {
    scanf("%d %d %d", &N, &E, &T);

    for(int i = 0; i < N; i++) {
        scanf("%d", &X[i]);
    }
    X[N] = E;

    bit1.init();
    bit2.init();

    dp[N] = 0;
    bit1.upd(N, dp[N] + 2 * X[N - 1] + X[N], 0, MN - 1, 1);
    bit2.upd(N, dp[N] + X[N], 0, MN - 1, 1);

    for(int i = N - 1; i >= 0; i--) {

        int s = i + 1, e = N, p = -1;
        while(s <= e) {
            int m = (s + e)>>1;

            if(T > 2 * (X[m - 1] - X[i])) {
                p = m;
                s = m + 1;
            }
            else e = m - 1;
        }

        dp[i] = bit1.quer(p + 1, N, 0, MN - 1, 1) - 3 * X[i];
        dp[i] = min(dp[i], bit2.quer(i + 1, p, 0, MN - 1, 1) - X[i] + T);

        bit1.upd(i, dp[i] + 2 * (i? X[i - 1] : 0) + X[i], 0, MN - 1, 1);
        bit2.upd(i, dp[i] + X[i], 0, MN - 1, 1);
    }

    printf("%lld", dp[0] + X[0]);
}

