typedef long long ll;
const int N=100010;
int e,n,wait;
int pos[N];
ll f[N];
int main(){
	scanf("%d%d%d",&n,&e,&wait);
	for (int i=1; i<=n; ++i) scanf("%d",&pos[i]);
	for (int i=1; i<=n; ++i) f[i]=LLONG_MAX/2;
	f[0]=0;
	for (int now=0; now<n; ++now){
		int x=-1;
		for (int l=now+1,r=n,mid=(l+r)>>1; l<=r; mid=(l+r)>>1)
		if (2*(pos[mid]-pos[now+1])<=wait) x=mid,l=mid+1; else r=mid-1;

		long long tmp=0;
		if (2*(pos[x]-pos[now+1])<=wait){
			tmp+=2ll*(pos[x]-pos[now+1]);
			tmp+=(ll)(wait-2*(pos[x]-pos[now+1]))*(x-now);
			//cerr<<"X"<<x<<endl;
			f[x]=min(f[x],f[now]+tmp);
			//cerr<<f[x]<<endl;
		}
		if (x<n){
			++x;
			//cerr<<"X"<<x<<endl;
			f[x]=min(f[x],f[now]+2ll*(pos[x]-pos[now+1]));
			//cerr<<f[x]<<endl;
		}
		f[now+1]=min(f[now+1],f[now]+wait);
		//cerr<<"now"<<now<<" "<<f[now]<<endl;
	}
	cout<<f[n]+e;
}

