            ll v = dp[j] - 3*S[j+1];
            f1[j] = v - 3*d;
            if(j > 0) chmin(f1[j], f1[j-1]);
            j++;
        }
        {
            ll v = dp[i] + T;
            f2[i] = v;
        }
        /*
        for(int j=0; j<=i; j++) {
            ll d = S[i+1] - S[j+1];
            ll c1 = dp[j] + 3*d, c2 = dp[j] + T + d;
