        }
        */

        // fprintf(stderr, "min_v_1 = %lld, min_v_2 = %lld\n", min_v_1, min_v_2);
        chmin(dp[i+1], min((j ? f1[j-1] + 3*(S[i+1]+d) : LONGINF), f2[j]) + e);
        // fprintf(stderr, "dp[%d] = %lld\n", i+1, dp[i+1]);

        d += S[i+2] - S[i+1];
        // fprintf(stderr, "d = %lld\n", d);
