  for (; x <= n; x += x & -x) {
    c[x] = min(c[x], y);
  }
}

int64 query(int x) {
  int64 ret = 1LL << 60;
  for (; x; x -= x & -x) {
    ret = min(ret, c[x]);
  }
  return ret;
}

