long long solve2(int n, int E, int T, int *x) {
    long long dp[n];
    memset(dp, 0, sizeof dp);
    for (int i = 0; i < n; i++) dp[i] = max(T, 2 * (x[i] - x[0]));
    for (int i = 1; i < n; i++) {
        for (int j = 0; j < i; j++) {
            dp[i] = min(dp[i], dp[j] + max(T, 2 * (x[i] - x[j+1])));
        }
    }
    return E + dp[n-1];
}

long long solve(int n, int E, int T, int *x) {
    long long dp[n], b[n+1];
    fill(dp, dp+n, 1LL<<60);
    fill(b, b+n+1, 1LL<<60);
    for (int i = 0, j = 0; i < n; i++) {
        while (2 * (x[i] - x[j]) > T) j++;
        // cerr << i << " " << j << endl;
        dp[i] = min(dp[i], (j > 0 ? dp[j-1] : 0) + T);
        dp[i] = min(dp[i], max(2LL * (x[i] - x[0]), (long long)T));
        for (int k = j-1; k > 0; k -= k&-k)
            dp[i] = min(dp[i], b[k] + 2 * x[i]);
        for (int k = i+1; k <= n; k += k&-k)
            b[k] = min(b[k], dp[i] - 2 * (i < n-1 ? x[i+1] : 0));
    }
    // for (int i = 0; i < n; i++) cerr << i << ' ' << dp[i] << endl;
    return E + dp[n-1];
}

int main() {
    int N, E, T; cin >> N >> E >> T;
    int x[N]; for (int i = 0; i < N; i++) cin >> x[i];
    long long ans = solve(N, E, T, x);
