			long long geko = -2 * inputs[now_back + 1] + dp[now_back];
			now_cost = min(now_cost, geko);
			now_back++;
		}
		dp[i] = min(dp[i],now_cost + 2 * inputs[i]);
		while (next.empty() == false&&2 * (inputs[i] - inputs[next.top().second])> t) {
			next.pop();
		}
		if (next.size() != 0) {
			dp[i] = min(dp[i], next.top().first + inputs[i] + t);
