    for (int i=1;i<=N;++i) cin >> x[i];
    vector<ll> x_2(N+1);
    for (int i=0;i<=N;++i) x_2[i]=x[i]*2;
    vector<ll> dp(N+1,IINF),dp0(N+1);
    SegmentTree<ll> seg(N+1,[](ll a,ll b){return min(a,b);},IINF);
    dp[0]=0; dp0[0]=-2*x[1];
    seg.update(0,0);
    for (int i=1;i<=N;++i){
        int pos=lower_bound(x_2.begin(),x_2.end(),x_2[i]-T)-x_2.begin()-2;
        // debug(i,pos);
        if (~pos) dp[i]=min(dp[i],dp0[pos]+3*x[i]);
        dp[i]=min(dp[i],seg.query(max(0,pos+1),i)+T+x[i]);
        if (i==N) continue;
        dp0[i]=min(dp0[i-1],dp[i]-x_2[i+1]-x[i]);
        seg.update(i,dp[i]-x[i]);
    }
    // cerr << dp << '\n';
