inline void update(int x,ll v)
{
	for(;x<=n;x+=lowbit(x))
		c[x]=min(c[x],v);
}
inline ll query(int x)
{
	ll ret=INF;
	for(;x;x-=lowbit(x))
		ret=min(ret,c[x]);
	return min(ret,(ll)-2*d[1]);
}
int main()
{
