	for(int i=1;i<=n;++i){
		while(2*(d[i]-d[p+1])>=t)++p;
		if(p>0)f[i]=min(f[i],query(p-1)+2*d[i]);
		f[i]=min(f[i],f[p]+t);
		update(i,f[i]-2*d[i+1]);
	}
	cout<<f[n]+e<<endl;
