template<typename T,const T id>
struct ConvexHullTrick{
    struct Line{
        T a,b;
        Line(T a=0,T b=0):a(a),b(b){}
        T get(T x){return max(tim+x-b+a,3*(x-b)+a);}
    };

    struct Node{
        Line l;
        Node *lhs,*rhs;
        Node(Line l):l(l),lhs(nullptr),rhs(nullptr){}
    };

    const int n;
    const vector<T> pos;
    Node* root;

    ConvexHullTrick(const vector<T> &pos):n(pos.size()),pos(pos),root(nullptr){}
    Node* insert(Node* p,int lb,int ub,Line& l){
        if(!p)return new Node(l);
        if(p->l.get(pos[lb])<=l.get(pos[lb])&&p->l.get(pos[ub])<=l.get(pos[ub]))return p;
        if(p->l.get(pos[lb])>=l.get(pos[lb])&&p->l.get(pos[ub])>=l.get(pos[ub])){
            p->l = l;
            return p;
        }
        int mid=(lb+ub)/2;
        if(p->l.get(pos[mid])>l.get(pos[mid]))swap(p->l , l);
        if(p->l.get(pos[lb])>=l.get(pos[lb])){
            p->lhs=insert(p->lhs,lb,mid,l);
        }
        else {
            p->rhs=insert(p->rhs,mid+1,ub,l);
        }
        return p;

    }
    void insert(T a,T b){
        Line l(a,b);
        root=insert(root,0,n-1,l);
    }
    T get(Node* p,int lb,int ub,int t){
        if(!p)return id;
        if(lb==ub)return p->l.get(pos[t]);
        int mid=(lb+ub)/2;
        if(t<=mid)return min(p->l.get(pos[t]),get(p->lhs,lb,mid,t));
        else return min(p->l.get(pos[t]),get(p->rhs,mid+1,ub,t));
    }

    T get(T x){
        int t=lower_bound(pos.begin(),pos.end(),x)-pos.begin();
        return get(root,0,n-1,t);
    }
};
int main(){
   int n;cin>>n;ll e;
    cin>>e>>tim;
    vector<ll> a(n+2);
    rep(i,n)cin>>a[i+1];
    a[n+1]=e;
    ConvexHullTrick<ll,longinf> cht(a);
    cht.insert(a[1],a[1]);
    rep(i,n){
        ll x=cht.get(a[i+1]);
        cht.insert(x+a[i+2]-a[i+1] ,a[i+2]);
    }
    cout<<cht.get(a[n])+a[n+1]-a[n]<<endl;
    return 0;
}

