LL ans;

int main() {
	scanf("%lld%lld%lld", &n, &e, &t);
	if(n > 2000) { return 1; }
	inc1(i, n) { scanf("%lld", &x[i]); }
	x[0] = 0;
	x[n + 1] = e;

	inc1(i, n) {
		ans += x[i] - x[i - 1];

		int jj = i;
		incII(j, i, n) {
			LL l = x[j] - x[i];
			LL w = l + t * (j - i + 1);
			LL b = l + max(2 * l, t);
			if(w < b) { break; }
			jj = j;
		}
		LL l = x[jj] - x[i];
		LL w = l + t * (jj - i + 1);
		LL b = l + max(2 * l, t);
		ans += min(w, b);

		i = jj;
	}
	ans += e - x[n];

	printf("%lld\n", ans);

	return 0;
}

