typedef long long LL;
struct sgt{
	LL mn[maxn*2];
	int id(int l,int r){return l+r|l!=r;}

	void build(int l,int r){
		mn[id(l,r)]=inf;
		if(l==r)return ;
		int mid=(l+r)>>1;
		build(l,mid);
		build(mid+1,r);
	}
	LL Q(int l,int r,int l0,int r0){
		if(l0<=l&&r0>=r)
			return mn[id(l,r)];
		int mid=(l+r)>>1;
		LL ans=inf;
		if(l0<=mid)ans=min(ans,Q(l,mid,l0,r0));
		if(r0>mid)ans=min(ans,Q(mid+1,r,l0,r0));
		return ans;
	}
	void C(int l,int r,int ps,LL val){
		if(l==r){
			mn[id(l,r)]=val;
			return ;
		}
		int mid=(l+r)>>1;
		if(ps<=mid)C(l,mid,ps,val);
		else C(mid+1,r,ps,val);
		mn[id(l,r)]=min(mn[id(l,mid)],mn[id(mid+1,r)]);
	}
}T1,T2;
