    VLL xs = in<LL>(N);

    VLL dp(N, INFL);
    rrep(i, N) {
        {
            LL t = T;
            if (i + 1 == N) t += E - xs[i];
            else t += dp[i + 1] + (xs[i + 1] - xs[i]);
            MINUD(dp[i], t);
