const int N = 100000 + 5;

LL n, l, r, ll, rr, E, T;
int q[N], qq[N];
LL s[N], f[N];

LL calc(int x) {
	return f[x] - 2LL * s[x + 1] - s[x];
}

LL calc2(int x) {
	return calc(x) + T + 2LL * s[x + 1];
}

void add(int x) {
	while (l <= r && calc(q[r]) > calc(x)) r--; 
	q[++r] = x;
}

void add2(int x) {
	while (ll <= rr && calc2(qq[rr]) > calc2(x)) rr--;
	qq[++rr] = x;
}

int main() {
	read(n), read(E), read(T);
	for (int i = 1; i <= n; i++) read(s[i]);
	for (int i = 1; i <= n; i++) f[i] = INF; f[0] = 0;
	l = 1, r = 0;
	ll = 1, rr = 0;
	add2(0);
	for (int i = 1, j = 0; i <= n; i++) {
		while (2LL * (s[i] - s[j + 1]) >= T) add(j++);	
		if (l <= r) {
			int k = q[l];
			f[i] = 3LL * s[i] + calc(k);
		}
		while (ll <= rr && T - 2LL * (s[i] - s[qq[ll] + 1]) < 0) ll++;
		if (ll <= rr) {
			int k = qq[ll];
			f[i] = min(f[i], 3LL * s[i] + calc(k) + T - 2LL * (s[i] - s[k + 1]));
		}
		add2(i);
		//printf("%lld\n", f[i]);
	}
	cout << f[n] + E - s[n] << endl;
	return 0;
}

