ll dp[SZ], push[SZ * 8][4], tree[SZ * 8][4];

void p(int v, int ind) {
	tree[v * 2 + 1][ind] += push[v][ind];
	tree[v * 2 + 2][ind] += push[v][ind];
	push[v * 2 + 1][ind] += push[v][ind];
	push[v * 2 + 2][ind] += push[v][ind];
	push[v][ind] = 0;
}

ll get(int v, int l, int r, int askl, int askr, int ind) {
	p(v, ind);
	if (l >= askr || r <= askl) {
		return INF;
	}

	if (l >= askl && r <= askr) {
		return tree[v][ind];
	}
	int mid = (l + r) / 2;
	ll q = get(v * 2 + 1, l, mid, askl, askr, ind), q2 = get(v * 2 + 2, mid, r, askl, askr, ind);
	return min(q, q2);
}

void upd(int v, int l, int r, int askl, int askr, int add, int ind) {
	p(v, ind);
	if (l >= askr || r <= askl) return;

	if (l >= askl && r <= askr) {
		tree[v][ind] += add;
		push[v][ind] += add;
		return;
	}

	int mid = (l + r) / 2;
	upd(v * 2 + 1, l, mid, askl, askr, add, ind);
	upd(v * 2 + 2, mid, r, askl, askr, add, ind);
	tree[v][ind] = min(tree[v * 2 + 1][ind], tree[v * 2 + 2][ind]);
}

