        for (int j = i; j < min(n, i + 500); j++) {
            ll delta = x[j] - x[i];
            ll add = x[i] - pos + delta + max(delta * 2, t);
            dp[j + 1] = min(dp[j + 1], dp[i] + add);
        }
    }
    cout << dp[n] + e - x[n - 1] << endl;
    return 0;
}

