#include <bits/stdc++.h>
using namespace std;

const int MAXN = 100005;

const long long INF = 1e18;

int n, a[MAXN], e, t;

#define lc (nd<<1)
#define rc (nd<<1|1)

struct node {
	long long x, y;
	node()
	{ x = y = INF; }
	node(long long _x, long long _y)
	{ x = _x, y = _y; }
	friend node operator + (const node &a, const node &b)
	{ return (node) {min(a.x, b.x), min(a.y, b.y)}; }
};

node dat[MAXN*4];
long long tag[MAXN*4];

inline void update(int nd)
{ dat[nd] = dat[lc]+dat[rc]; }
inline void put_tag(int nd, int dt)
{ dat[nd].x += dt, dat[nd].y += dt, tag[nd] += dt; }
inline void pdw(int nd)
{
	put_tag(lc, tag[nd]);
	put_tag(rc, tag[nd]);
	tag[nd] = 0;
}
inline void modify(int nd, int L, int R, int opl, int opr, int dt)
{
	pdw(nd);
	if (L == opl && R == opr) put_tag(nd, dt);
	else {
		int mid = (L+R)>>1;
		if (opr <= mid) modify(lc, L, mid, opl, opr, dt);
		else if (opl > mid) modify(rc, mid+1, R, opl, opr, dt);
		else modify(lc, L, mid, opl, mid, dt), modify(rc, mid+1, R, mid+1, opr, dt);
		update(nd);
	}
}
inline void modify(int nd, int L, int R, int pos, const node &pt)
{
	pdw(nd);
	if (L == R) dat[nd] = pt;
	else {
		int mid = (L+R)>>1;
		if (pos <= mid) modify(lc, L, mid, pos, pt);
		else modify(rc, mid+1, R, pos, pt);
		update(nd);
	}
}
inline node query(int nd, int L, int R, int opl, int opr)
{
	if (opl > opr) return (node) {INF, INF}; 
	pdw(nd);
	if (L == opl && R == opr) return dat[nd];
	else {
		int mid = (L+R)>>1;
		if (opr <= mid) return query(lc, L, mid, opl, opr);
		else if (opl > mid) return query(rc, mid+1, R, opl, opr);
		else return query(lc, L, mid, opl, mid)+query(rc, mid+1, R, mid+1, opr);
	}
}
