		dpv = min(query(root, 0, n, 0, pos).y+3ll*a[i]-a[i-1], query(root, 0, n, pos+1, i-1).x+a[i]-a[i-1]+t);
		modify(root, 0, n, i, (node) {dpv, dpv-2*a[i+1]});
		modify(root, 0, n, 0, i-1, a[i]-a[i-1]);
