    for (int i = 0; i < n - 1; i++) d[i] = x[i + 1] - x[i];
    for (int i = 0; i < n; i++) ds[i + 1] = ds[i] + d[i];

    fill_n(dp, 101010, 1e17);
    dp[0] = 0;

    RMQ L(n + 1), R(n + 1);
    L.update(0, 0);
    R.update(0, 0);

    for (int i = 1; i <= n; i++) {
        int ok = i;
        int ng = -1;
        while (ok - ng > 1) {
            int mid = (ok + ng) / 2;
            if (T >= 2 * (ds[i - 1] - ds[mid])) ok = mid;
            else ng = mid;
        }
        chmin(dp[i], L.minimum(ok, i) + T);
        chmin(dp[i], R.minimum(0, ok) + 2 * ds[i - 1]);
        L.update(i, dp[i]);
        R.update(i, dp[i] - 2 * ds[i]);
    }

    cout << dp[n] + E << endl;
}

