#include <bits/stdc++.h>
using namespace std;
#define int long long
const int MAXN = 1e6 + 10, md = 1e9 + 7;
int p = 31;
string s[3];
int n[3];
int pw[MAXN], h[3][MAXN];
vector <pair <int, int>> suffix;
int get(int l, int r, int index) {
    return h[index][r] - h[index][l] * pw[r - l];
}
int length(pair <int, int> a, pair <int, int> b) {
    int l = 0, r = min(n[a.second] - a.first, n[b.second] - b.first) + 1;
    while (r - l > 1) {
        int mid = (l + r) / 2;
        if (get(a.first, a.first + mid, a.second) == get(b.first, b.first + mid, b.second))
            l = mid;
        else
            r = mid;
    }
    return l;
}
bool cmp(pair <int, int> a, pair <int, int> b) {
    int l = length(a, b);
    if (l == n[a.second] - a.first)
        return true;
    if (l == n[b.second] - b.second)
        return false;
    return s[b.second][b.first + l] > s[a.second][a.first + l];
}
int cnt[3][MAXN][3];
int in[3][MAXN];
struct K{
    pair <int, int> left, right;
    int l;
} a1;
bool cmp1(K a, K b) {
    return a.l > b.l;
}
pair <int, int> par[3][MAXN];
pair <int, int> find(pair <int, int> a) {
    pair <int, int> now = par[a.second][a.first];
    if (par[now.second][now.first] == now)
        return now;
    return par[a.second][a.first] = find(now);
}
int32_t main () {
    ios_base::sync_with_stdio(false), cin.tie(0), cout.tie(0);
    pw[0] = 1;
    for (int i = 1; i < MAXN; ++i)
        pw[i] = pw[i - 1] * p;
    cin >> s[0] >> s[1] >> s[2];
    for (int now = 0; now < 3; ++now) {
        n[now] = s[now].size();
        for (int i = 1; i <= n[now]; ++i) {
            h[now][i] = h[now][i - 1] * p + s[now][i - 1];
            suffix.push_back({i - 1, now});
            ++cnt[now][i - 1][now];
            par[now][i - 1] = {i - 1, now};
        }
    }
    sort(suffix.begin(), suffix.end(), cmp);
    vector <K> vctr;
    int sz = suffix.size();
    for (int i = 0; i < sz - 1; ++i) {
        a1.left = suffix[i];
        a1.right = suffix[i + 1];
        a1.l = length(suffix[i], suffix[i + 1]);
        // cout << a1.l << ' ';
        vctr.push_back(a1);
    }
    sort(vctr.begin(), vctr.end(), cmp1);
    int point = 0, now = 0;
    vector <int> res;
    for (int l = n[0]; l; --l) {
        while (point < sz - 1 && vctr[point].l >= l) {
            pair <int, int> v = find(vctr[point].left), u = find(vctr[point].right);
            if (u == v)
                continue;
            int cur = 1;
            for (int i = 0; i < 3; ++i)
                cur *= cnt[v.second][v.first][i] + cnt[u.second][u.first][i];
            cur %= md;
            int cpy = cur;
            cur += (in[v.second][v.first] + in[u.second][u.first]) * (md - 1) % md;
            if (cur >= md)
                cur %= md;
            now += cur;
            if (now >= md)
                now %= md;
            par[v.second][v.first] = u;
            in[u.second][u.first] = cpy;
            for (int i = 0; i < 3; ++i)
                cnt[u.second][u.first][i] += cnt[v.second][v.first][i];
            ++point;
        }
        res.push_back(now);
    }
    int mn = min(n[0], min(n[1], n[2]));
    reverse(res.begin(), res.end());
    for (int i = 0; i < mn; ++i)
        cout << res[i] << ' ';
    return 0;
}

