{
    n = read();
    for(int i = 2; i <= n; ++i)
    {
        int x = read(); w[i] = read();
        ch[c[x]++][x] = i;
    }
    dfs(1);
    while(l <= r)
    {
        mid = (l+r)>>1;
        if(valid(1)) ans = mid, r = mid-1;
        else l = mid+1;
    }
    printf("%lld\n", ans);
    return 0;
}

