const int maxn = 1e5 + 100;
const ll inf = 1e18;

ll x[maxn], dp[maxn];

class node {
	public:
		ll mnm;
		ll lzy;
		ll cng;
		node () {
			mnm = inf;
		}
};

node seg[4 * maxn];

void propagate (int, int, int);

ll get (int id, int L, int R, int l, int r) {
	if (L == l and R == r)
		return seg[id].mnm;
	propagate (id, L, R);
	int mid = (L + R) >> 1;
	ll ret = inf;
	if (mid > l)
		ret = min(ret, get(2 * id + 0, L, mid, l, min(mid, r)));
	if (mid < r)
		ret = min(ret, get(2 * id + 1, mid, R, max(l, mid), r));
	return ret;
}

void update(int id, int L, int R, int l, int r, ll val) {
	if (L == l and R == r) {
		seg[id].mnm += val;
		seg[id].lzy += val;
		return;
	}
	propagate (id, L, R);
	int mid = (L + R) >> 1;
	if (mid > l)
		update(2 * id + 0, L, mid, l, min (mid, r), val);
	if (mid < r)
		update(2 * id + 1, mid, R, max (l, mid), r, val);
	seg[id].mnm = min (seg[2 * id + 0].mnm, seg[2 * id + 1].mnm);
}

void change(int id, int L, int R, int l, int r, ll val) {
	if (L == l and R == r) {
		seg[id].mnm = val;
		seg[id].cng = val;
		seg[id].lzy = 0;
		return;
	}
	propagate(id, L, R);
	int mid = (L + R) >> 1;
	if (mid > l)
		change(2 * id + 0, L, mid, l, min (mid, r), val);
	if (mid < r)
		change(2 * id + 1, mid, R, max (l, mid), r, val);
	seg[id].mnm = min(seg[2 * id + 0].mnm, seg[2 * id + 1].mnm);
}

void propagate (int id, int L, int R) {
	int mid = (L + R) >> 1;
	if (seg[id].cng != 0)
		change(2 * id + 0, L, mid, L, mid, seg[id].cng);
	if (seg[id].lzy != 0)
		update(2 * id + 0, L, mid, L, mid, seg[id].lzy);
	seg[id].lzy = seg[id].cng = 0;
}

