const int N=100000+19;
const ll oo=1ll<<50;

ll f[N];
int d[N],E,T,n,l,r,res;

ll Qv;
int Ql,Qr,Qx;

struct SegTree{
	ll mn[N*4];
	SegTree(){
		For(i,0,N*4) mn[i]=oo;
	}
	void Modify(int x,int L,int R){
		mn[x]=min(mn[x],Qv);
		if (L==R) return;
		if (Qx<=Mid) Modify(Lsn,L,Mid);else Modify(Rsn,Mid+1,R);
	}
	ll Query(int x,int L,int R){
		if (Ql<=L&&R<=Qr) return mn[x];
		ll res=oo;
		if (Ql<=Mid) res=min(res,Query(Lsn,L,Mid));
		if (Qr>Mid) res=min(res,Query(Rsn,Mid+1,R));
		return res;
	}
} S1,S2;

int main(){
	n=IN(),E=IN(),T=IN();
	For(i,1,n+1) d[i]=IN();
	For(i,0,N) f[i]=oo;
	f[0]=0;
	For(i,1,n+1){
		Qx=i,Qv=f[i-1]+d[i]-d[i-1]-3ll*d[i];
		S1.Modify(1,1,n);
		Qx=i,Qv=f[i-1]+d[i]-d[i-1]-d[i];
		S2.Modify(1,1,n);

		l=1,r=i;res=0;
		while (l<=r){
			int mid=(l+r>>1);
			if (2*(d[i]-d[mid])>T) res=mid,l=mid+1;else r=mid-1;
		}
		Ql=1,Qr=res;
		if (Ql<=Qr) f[i]=min(f[i],S1.Query(1,1,n)+3ll*d[i]);
		Ql=res+1,Qr=i;
		if (Ql<=Qr) f[i]=min(f[i],S2.Query(1,1,n)+T+d[i]);
