		For(j,1,i+1){
			f[i]=min(f[i],f[j-1]+d[j]-d[j-1]+max(2*(d[i]-d[j]),T)+d[i]-d[j]);
		}
		*/
	}
	cout<<f[n]+E-d[n]<<endl;
}

