inline void chk_min(ll &a,ll b){ if(a>b) a=b;}

struct Queue
{
	int q[MAXN],hd,tl;
	Queue(void){ hd=tl=0;}
	inline bool empty(void){ return hd>=tl;}
	inline int front(void){ return q[hd+1];}
	inline int back(void){ return q[tl];}
	inline void push(int x){ q[++tl]=x;}
	inline void pop(void){ ++hd;}
}q;

int a[MAXN];
ll dp[MAXN],preMn[MAXN];

int main(void)
{
	int n,e,t;
	scanf("%d%d%d",&n,&e,&t);
	for(int i=1; i<=n; ++i) scanf("%d",&a[i]);

	memset(dp,0x3f,sizeof(dp));

	dp[1]=t;
	q.push(1);
	preMn[1] = min(dp[1]-2*a[2], 0ll);
	for(int i=2; i<=n; ++i)
	{
		while(!q.empty() && (a[i]-a[q.front()+1])*2 > t) q.pop();

		dp[i]=dp[i-1]+t;
		chk_min(dp[i], max(t, (a[i]-a[1])*2));

		if(!q.empty())
			chk_min(dp[i], dp[q.front()]+t),
			chk_min(dp[i], preMn[q.front()-1]+2*a[i]);
		else chk_min(dp[i], preMn[i-1]+2*a[i]);

		preMn[i] = min(preMn[i-1], dp[i]-2*a[i+1]);
		q.push(i);
	}
	printf("%lld\n",dp[n]+e);
	return 0;
}

