#include <cstdio>
#include <cstring>
#include <algorithm>
using namespace std;

const int N = 100005;
const long long INF = (1LL<<60);
int n, T, E;
int X[N];

#define lson(x) (1<<x)
#define rson(x) ((1<<x)|1)
struct Node {
    int l, r;
    long long Min[2], lazy;
    void gao(int f){
        lazy += f;
        Min[0] += f;
        Min[1] += f;
    }
} node[N * 4];

void pushdown(int x) {
    if (node[x].lazy) {
        node[lson(x)].gao(node[x].lazy);
        node[rson(x)].gao(node[x].lazy);
        node[x].lazy = 0;
    }
}

void pushup(int x) {
    for (int i = 0; i < 2; i++)
        node[x].Min[i] = min(node[lson(x)].Min[i], node[rson(x)].Min[i]);
}

void build(int l, int r, int x = 1) {
    node[x].l = l; node[x].r = r;
    node[x].lazy = 0;
    if (l == r) {
        node[x].Min[0] = 0;
        node[x].Min[1] = X[l];
        return;
    }
    int mid = (l + r)>>1;
    build(l, mid, lson(x));
    build(mid + 1, r, rson(x));
    pushup(x);
}

void add(int l, int r, int w, int x = 1) {
    if (node[x].l >= l && node[x].r <= r) {
        node[x].gao(w);
        return;
    }
    int mid = (node[x].l + node[x].r)>>1;
    pushdown(x);
    if (l <= mid) add(l, r, w, lson(x));
    if (r > mid) add(l, r, w, rson(x));
    pushup(x);
}

long long query(int l, int r, int tp, int x = 1){
    if (l > r) return INF;
    if (node[x].l >= l && node[x].r <= r) {
        return node[x].Min[tp];
    }
    int mid = (node[x].l + node[x].r)>>1;
    pushdown(x);
    long long ans = INF;
    if (l <= mid) ans = min(ans, query(l, r, tp, lson(x)));
    if (r > mid) ans = min(ans, query(l, r, tp, rson(x)));
    pushup(x);
}

long long dp[N];
int main() {
    while (~scanf("%d%d%d", &n, &E, &T)) {
        for (int i = 1; i <= n; i++) scanf("%d", &X[i]);
        build(0, n);
        int v = 0;
        for (int i = 1; i <= n; i++) {
            dp[i] = INF;
            for (int j = 0; j < i; j++) {
                dp[i] = min(dp[i], dp[j] + max((X[i] - X[j + 1]) * 2, T) + X[i] - X[j]);
            }
          //  printf("%d %lld\n", i, dp[i]);
        }
        printf("%lld\n", dp[n] + E - X[n]);
    }
    return 0;
}

