    if (ss>=us && se<=ue){
        seg2[si] += diff;
        if (ss != se){
            lazy2[si*2 + 1]   += diff;
            lazy2[si*2 + 2]   += diff;
        }
        return;
    }
    ll mid = (ss+se)/2;
    updateRangeUtil2(si*2+1, ss, mid, us, ue, diff);
    updateRangeUtil2(si*2+2, mid+1, se, us, ue, diff);
    seg2[si] = min(seg2[si*2+1] , seg2[si*2+2]);
}

void updateRange2(ll us, ll ue, ll diff){
