multiset < long long > S1, S2;

void push (int lin, long long val)
{
    if (lin == 1) S1.insert (val);
    else S2.insert (val);
}

void pop (int lin, long long val)
{
    if (lin == 1) S1.erase (S1.find (val));
    else S2.erase (S2.find (val));
}

long long getVal (int lin, int k)
{
    if (lin == 1) return (long long) dp[k - 1] - x[k] - x[k] - x[k - 1];
    return (long long) dp[k - 1] - x[k - 1] + T;
}

