	// 2 segtrees: one record dp[j] - 2 * a[j + 1] - a[j], one record dp[j] - a[j].
	update(0, 0, n - 1, 0, dp[0] - 2 * a[1], 0);
	for (ll i = 1; i <= n; i++){
		dp[i] = 1e18;
		ll lw; // lw: (a[i] - a[lw]) * 2 <= t, (a[i] - a[lw - 1]) * 2 > t. (lw - 1 -> 0)
		// manual binsearch? lw >= l, <= r
		ll l = 0, r = i - 1;
		while (l < r){
			ll mid = (l + r) / 2;
			if ((a[i] - a[mid + 1]) * 2 <= t) r = mid;
			else l = mid + 1;
		}
		lw = l;
		// so (dp[j] - 2 * a[j + 1] - a[j]) + 3 * a[i] with a from 0 to lw - 1, (dp[j] - a[j]) + a[i] + t to lw to i - 1
		dp[i] = min(query(0, 0, n - 1, 0, lw - 1, 0) + 3 * a[i], query(0, 0, n - 1, lw, i - 1, 1) + a[i] + t);
		update(0, 0, n - 1, i, dp[i] - 2 * a[i + 1] - a[i], 0); update(0, 0, n - 1, i, dp[i] - a[i], 1);
		// for (ll j = 0; j < i; j++) dp[i] = min(dp[i], dp[j] + max(2 * (a[i] - a[j + 1]), t) + a[i] - a[j]);
	}
	// hj, optimize nao
	cout << dp[n] + (e - a[n]) << endl;
}

