#define N 100001
struct SegmentTree {
private:
    int n; vector<ll> node;
public:
    SegmentTree(int sz) {
        n = 1; while(n < sz) n *= 2;
        node.resize(2*n-1, INF);
    }
    void update(int x, ll val) {
        x += (n - 1);
        node[x] = val;
        while(x > 0) {
            x = (x - 1) / 2;
            node[x] = min(node[2*x+1], node[2*x+2]);
        }
    }
    ll getmin(int a, int b, int k=0, int l=0, int r=-1) {
        if(r < 0) r = n;
        if(r <= a || b <= l) return INF;
        if(a <= l && r <= b) return node[k];
        ll vl = getmin(a, b, 2*k+1, l, (l+r)/2);
        ll vr = getmin(a, b, 2*k+2, (l+r)/2, r);
        return min(vl, vr);
    }
    ll rmq(int a,int b){
        return getmin(a,b,0,0,-1);
    }
};
main(){
    ll n=lin(),e=lin(),t=lin();
    ll x[N]={};
    rep2(i,1,n)x[i]=lin();
    ll dp[N]={};
    SegmentTree seg1(n+1),seg2(n+1);
    seg1.update(0,-x[1]*2);
    seg2.update(0,0);
    rep2(i,1,n){
        int l=0,r=i-1;
        while(l<r-1){
            int mid=(l+r)/2;
            if(t-(x[i]-x[mid+1])*2>0){
                r=mid;
            }
            else l=mid;
        }
        ll m1=seg1.rmq(0,r);
        ll m2=seg2.rmq(r,i)+t-x[i]*2;
        dp[i]=3*x[i]+min(m1,m2);
        seg1.update(i,dp[i]-(x[i]+2*x[i+1]));
        seg2.update(i,dp[i]-x[i]);
    }
    rep(i,n)cout<<dp[i]<<endl;
    cout<<dp[n]+(e-x[n]);
}

