void work ()
{
	// main work

	dp[0] = 0;
	int l = 1, r = 0;
	int nl = 0;
	LL vl = LINF;
	q[++r] = MP ( dp[0] - a[1], 0 );
	lpi ( i, 1, n ){
		while ( ( ( a[i] - a[nl+1] ) << 1 ) > T ){
			vl = min ( vl, dp[nl] - ( a[nl+1] << 1 ) );
			++nl;
		}
		while ( l <= r && ( ( a[i] - a[q[l].SS+1] ) << 1 ) > T ) ++l;
		dp[i] = min ( vl + ( a[i] << 1 ), ( l <= r ) ? ( q[l].FF + a[i] + T ) : LINF );
		while ( l <= r && ( q[r].FF >= dp[i] - a[i+1] ) ) --r;
		q[++r] = MP ( dp[i] - a[i+1], i );
	}

//	lpi ( i, 1, n ) cerr << dp[i] << " "; cerr << endl;

	cout << dp[n] + L << endl;
}

