const int maxn = 1e5 + 1;
const ll inf = 2e18, mod = 1e9 + 7;

int n, x[maxn], dp[maxn], seg[4 * maxn], E, T, ind;

void build(int u, int l, int r)
{
	if (r - l == 1)
	{
		seg[u] = -x[l] * 2;
		return;
	}
	int mid = (l + r) / 2;
	build(lchild(u), l, mid);
	build(rchild(u), mid, r);
	seg[u] = min(seg[lchild(u)], seg[rchild(u)]);
}

void update(int u, int l, int r, int x, int val)
{
	if (r - l == 1)
	{
		seg[u] += val;
		return;
	}
	int mid = (l + r) / 2;
	if (x < mid)
		update(lchild(u), l, mid, x, val);
	else
		update(rchild(u), mid, r, x, val);
	seg[u] = min(seg[lchild(u)], seg[rchild(u)]);
}

int query(int u, int l, int r, int b, int e)
{
	if (r == l)
		return mod;
	if (b <= l && r <= e)
		return seg[u];
	int mid = (l + r) / 2, tmp = inf;
	if (b < mid)
		tmp = min(tmp, query(lchild(u), l, mid, b, e));
	if (e > mid)
		tmp = min(tmp, query(rchild(u), mid, r, b, e));
	return tmp;
}

int main()
{
	ios_base::sync_with_stdio(false);
	cin.tie(0);

	cin >> n >> E >> T;
	fori (i, 0, n)
		cin >> x[i];
	sort(x, x + n);
	build(0, 0, n);
	fori (i, 0, n)
	{
		ind = lower_bound(x, x + n, x[i] - T / 2) - x;
		dp[i] = min(2 * x[i] + query(0, 0, n, 0, ind), T + (ind ? dp[ind - 1] : 0));
		if (i)
			update(0, 0, n, i, dp[i - 1]);
	}
	cout << dp[n - 1] + E << endl;
}

