/*!* [binsearch] *!*/
/*+* äºŒåˆ†æŽ¢ç´¢ *+*/
// judge ã‚’æº€ãŸã™ã‚ˆã†ãª(l,r]å†…ã®ä¸‹é™ã‚’æ±‚ã‚ã‚‹
// l:[X] å®šç¾©åŸŸã®ä¸‹ç«¯
// r:[X] å®šç¾©åŸŸã®ä¸Šç«¯
// judge:[F = X -> bool] å€¤ X ã‚’è©•ä¾¡ã™ã‚‹é–¢æ•°
// lower:[bool] false ã«ã™ã‚‹ã¨ä»£ã‚ã‚Šã«[l,r)å†…ã®ä¸Šé™ã‚’æ±‚ã‚ã‚‹
// eps:[X] çµ‚äº†è·é›¢

template <typename X, typename F>
X BinSearch(X l, X r, F judge, bool lower = true, X eps = 1) {
    while (r - l > eps) {
        X m = (r + l) / 2;
        if (judge(m) == lower) {
            r = m;
        } else {
            l = m;
        }
    }
    return lower ? r : l;
}

/*!* [segtree] *!*/
/*+* ã‚»ã‚°ãƒ¡ãƒ³ãƒˆæœ¨ *+*/
// Tã¯ $f : T \to T$ ã«ã¤ã„ã¦ãƒ¢ãƒŽã‚¤ãƒ‰ã§ã‚ã‚‹å¿…è¦ãŒã‚ã‚‹ã€‚
// Fã®åž‹ã‚’decltype(f)ã¨ã™ã‚‹ã¨é«˜é€Ÿã«ãªã‚‹ã€‚
template <typename T, typename F = function<T(T, T)>>
struct SegTree {
    int N;
    vector<T> array; 
    const F f;
    const T id;
    SegTree() {}
    // len : ãƒ‡ãƒ¼ã‚¿é•·, f : äºŒé …æ¼”ç®—, identity: åˆæœŸå€¤(å˜ä½å…ƒ)
    SegTree(int len, const F f, const T &id) : f(f), id(id) {
        for(N = 1; N < len; N <<= 1);
        array.reserve(2 * N - 1);
        array.assign(2 * N - 1, id);
    }

    // å€¤ã®æ›´æ–° O(\log N)$
    // k : æ·»å­—, val : å€¤
    void update(int k, T val) {
        k += N;
        array[k] = val;
        while(k >>= 1) array[k] = f(array[2 * k], array[2 * k + 1]);
    }

    // å€¤ã®å–ã‚Šå‡ºã— O(1)$
    // k : æ·»å­—
    T operator[] (int k) { return array[k + N]; }

    // ç¯„å›²æ¤œç´¢ O(\log N)$
    // [L : R] ã®ç¯„å›²ã«ã¤ã„ã¦ã€f( [L : R] ) ã‚’æ±‚ã‚ã‚‹
    T query(int l, int r) {
        T L = id, R = id;
        for(l += N, r += N, r++; l < r; l >>= 1, r >>= 1) {
            if(l & 1) L = f(L, array[l++]);
            if(r & 1) R = f(array[--r], R);
        }
        return f(L, R);
    }
};

int main() {
    const i64 INF = 1e12;
    i64 N, E, T;
    cin >> N >> E >> T;
    vector<i64> x = {0};
    for(int i = 0; i < N; i++) {
        i64 a;
        cin >> a;
        x.push_back(a);
    }
    auto f_min = [](i64 a, i64 b) -> i64 { return min(a, b); };
    vector<i64> dp(N + 1, INF);
    dp[0] = 0;
    SegTree<i64, decltype(f_min)> seg_front(N + 1, f_min, INF),
                                   seg_back(N + 1, f_min, INF);

    seg_front.update(0, -2 * x[1]);
    seg_back.update(0, 0);
    for(int k = 1; k <= N; k++) {
        int j = BinSearch<int>(-1, k, [&](int i) -> bool {
            return 2 * (x[k] - x[i + 1]) < T;
        });
        if (j > 0) {
            min_assign(dp[k], 3 * x[k] + seg_front.query(0, j - 1));
        }
        min_assign(dp[k], x[k] + T + seg_back.query(j, N));
        if(k < N) {
            seg_front.update(k, dp[k] - x[k] - 2 * x[k + 1]);
            seg_back.update(k, dp[k] - x[k]);
        }
    }
    cout << dp[N] + E - x[N] << endl;
    return 0;
}

