ll get(int v, int tl, int tr, int l, int r,int k){
	if(l > r)
		return inf;
	if(tl == l && tr == r)
		return t[v][k];
	int tm = (tl + tr) / 2;
	return min(get(v + v, tl, tm , l, min(tm, r), k), get(v + v + 1, tm + 1, tr, max(tm + 1, l), r, k));
}

void upd(int v, int tl, int tr, int pos, ll val,int k){
	t[v][k] = min(t[v][k], val);
	if(tl == tr)
		return;
	int tm = (tl + tr) / 2;
	if(pos <= tm)                  
		upd(v + v, tl, tm, pos, val, k);
	else
