inline void Modify(int now,int l,int r,int pos,ll d) {
	if(l==r) {S[now]=d;return ;}
	int mid=(l+r)>>1;
	if(pos<=mid) Modify(now<<1,l,mid,pos,d);
	else Modify(now<<1|1,mid+1,r,pos,d);
	S[now]=min(S[now<<1],S[now<<1|1]);
}
inline ll Qry(int now,int l,int r,int x,int y){
	if(x<=l&&r<=y) return S[now];
	ll res=1e18;
	int mid=(l+r)>>1;
	if(x<=mid) res=min(res,Qry(now<<1,l,mid,x,y));
	if(y>mid) res=min(res,Qry(now<<1|1,mid+1,r,x,y));
	return res;
}
