const int MAXN = 100007;
const int INF = 1000000007;
const LL LINF = INF * (LL)INF;

LL A[MAXN], DP[MAXN], T1[4 * MAXN], T2[4 * MAXN];

LL query(int v, int tl, int tr, int l, int r, LL * const T)
{
	if (l > r) return LINF;
	if (l == tl && r == tr) return T[v];
	int tm = (tl + tr) / 2;
	return min(query(v * 2, tl, tm, l, min(r, tm), T), query(v * 2 + 1, tm + 1, tr, max(l, tm + 1), r, T));
}

void update(int v, int tl, int tr, int pos, LL val, LL * const T)
{
	if (pos < tl || pos > tr) return;
	if (tl == tr)
	{
		T[v] = val;
		return;
	}
	int tm = (tl + tr) / 2;
	update(v * 2, tl, tm, pos, val, T);
	update(v * 2 + 1, tm + 1, tr, pos, val, T);
	T[v] = min(T[v * 2], T[v * 2 + 1]);
}

