	assert(n <= 2000);
	vector<ll> a(n + 1);
	a[0] = 0;
	for (int i = 1; i <= n; i++) cin >> a[i];
	vector<vector<int>> ch(n + 2, vector<int>());
	rep(i, n) {
		ll sum = 2 * a[i + 1] + t;
		int ng = -1, ok = n + 1;
		while (ok - ng > 1) {
			int mid = (ok + ng) / 2;
			if (2 * a[mid] > sum) ok = mid;
			else ng = mid;
		}
		ch[ok].pb(i);
	}
	segtree<ll> dp(n + 1), seg(n + 1);
	dp.change(0, 0);
	for (int i = 1; i <= n; i++) {
		for (int j : ch[i]) {
			seg.change(j, dp[j] - 2 * a[j + 1]);
			dp.change(j, INF);
		}
		ll mi = dp.get(0, i) + t;
		chmin(mi, seg.get(0, i) + 2 * a[i]);
		dp.change(i, mi);
	}
	cout << dp[n] + e << '\n';
}

