ll dfs(int now) {
	ll& ret = dp[now];
	if (ret >= 0) return ret;
	if (now == N) return ret = 0;
	ret = 1ll<<60;
	for (int j = now; j < N; j++) {
		ll dist = X[j] - X[now];
		ll cost = (dist*2 > T) ? dist*3 : T+dist;
		ret = min(ret, cost + dfs(j+1) + (X[j+1] - X[j]));
	}
	return ret;
}

