typedef long long ll;

ll p[2123];
ll cache[2123][2123];
bool seen[2123][2123];
ll n, e, t;

//x = curpos
//y = first coin
//x >= y
ll dp(ll x, ll y)
{
	if (x == n) return 0;
	if (seen[x][y]) return cache[x][y];
	seen[x][y] = true;

	ll ans1 = -1, ans2 = -1;
	if (x + 1 < n)
		ans1 = p[x+1] - p[x] + dp(x+1, y);
	ans2 = p[x] - p[y] + max(0ll, t - 2*(p[x] - p[y])) + p[x+1] - p[y] + dp(x+1, x+1);
	if (ans1 > -1) ans2 = min(ans2, ans1);
	cache[x][y] = ans2;
	return ans2;
}

