ll solve(int pos, int time){
    if(pos == 0){
        return xs[0] + max(0, t - time);
    }
    ll dx = xs[pos] - xs[pos - 1];
    return min(
            solve(pos - 1, 0) + dx + max(0, (t - time)),
            solve(pos - 1, 2 * dx) + 3 * dx);
}

