		tree1.update(i, mini - vec[i + 1] * 3);
		tree2.update(i, mini - vec[i + 1]);
	}
	printf("%lld\n", tree2.get(vec.size() - 2, vec.size() - 2, 1, 0, SIZE - 1) + len);
}

