	vector<vector<array<ll, 2>>> posibilitati;

	void remove_stupidity(vector<array<ll, 2>>& v){
		if(v.empty()) return;
		int i0 = 0;
		for(int i = 1; i < (int)v.size(); ++i){
			if(v[i0][0] < v[i][0] && v[i0][1] > v[i][1]){
				v[++i0] = v[i]; }
			else if(v[i0][0] == v[i][0] && v[i0][1] > v[i][1]){
				v[i0] = v[i]; } }

		v.erase(begin(v) + i0 + 1, end(v)); }

	void dfs(const int cur, const ll maxlen){
		posibilitati[cur].clear();

		if(fii[cur].empty()){
			posibilitati[cur] = vector<array<ll, 2>> { array<ll, 2> { 0, 0 }};
			return; }

		dfs(fii[cur][0], maxlen), dfs(fii[cur][1], maxlen);
		if(posibilitati[fii[cur][0]].empty() || posibilitati[fii[cur][1]].empty()) return;

		const ll ca = dist[cur][fii[cur][0]],
			  cb = dist[cur][fii[cur][1]];

		auto it = --end(posibilitati[fii[cur][1]]);
		vector<array<ll, 2>> tmpa, tmpb;
		for(const auto& pa : posibilitati[fii[cur][0]]){
			while(it > begin(posibilitati[fii[cur][1]]) && ca + cb + pa[0] + (*it)[0] > maxlen){
				--it; }
			if(ca + cb + pa[0] + (*it)[0] <= maxlen){
				tmpa.push_back(array<ll, 2> { pa[1]    + ca, (*it)[1] + cb });
				if(pa[1] + ca != (*it)[1] + cb) tmpb.push_back(array<ll, 2> { (*it)[1] + cb, pa[1]    + ca }); } }
		if(!is_sorted(begin(tmpa), end(tmpa))) reverse(begin(tmpa), end(tmpa));
		if(!is_sorted(begin(tmpb), end(tmpb))) reverse(begin(tmpb), end(tmpb));

		merge(begin(tmpa), end(tmpa), begin(tmpb), end(tmpb), back_inserter(posibilitati[cur]));
		remove_stupidity(posibilitati[cur]); }

