LL getdist(vector<LL> &x, LL T, int i, int j, bool& param) {
	LL ret = 0;

	ret = x[j] - x[i] ; //+ (x[j] - x[i+1]) * 2;
	ret += max( T,  (x[j] - x[i+1]) * 2);
	if(T < (x[j] - x[i]) * 2) param = true;
	else param = false;
	return ret;
}


