void update(int id,int l,int r,int l2,int r2,long long int val){
if(l>=r2||r<=l2){
	return;
}	
if(l2<=l&&r<=r2){
	if(seg[id].first==0||seg[id].first>=val){
		seg[id]={val,l2};
		return ;
	}
}
int mid=(l+r)>>1;
update(id<<1,l,mid,l2,r2,val);
update((id<<1)|1,mid,r,l2,r2,val);
seg[id]=min(seg[id<<1],seg[(id<<1)|1]);
}
pair<long long int,int>get(int id,int l,int r,int l2,int r2){
	if(l>=r2||r<=l2){
		return {(long long int)1e18,1000};
	}
	if(l2<=l&&r<=r2){
		return seg[id];
	}
	int mid=(l+r)>>1;
	return min(get(id<<1,l,mid,l2,r2),get((id<<1)|1,mid,r,l2,r2));
}
