    // å…¨ã¦ã®å€¤ã‚’INT_MAXã«
    for(int i = 0; i < 2 * nn - 1; i++)for(int j = 0; j < MAX_M; j++)dat[i][j] = 123456789123456789;
}

// kç•ªç›®ã®å€¤(0-indexed)ã‚’aã«å¤‰æ›´
void update(int k, ll a, int m){
    // è‘‰ã®æŽ¥ç‚¹
    k += nn - 1;
    dat[k][m] = a;
    // ç™»ã‚ŠãªãŒã‚‰æ›´æ–°
    while(k > 0){
        k = (k - 1) / 2;
        dat[k][m] = min(dat[k * 2 + 1][m], dat[k * 2 + 2][m]);
    }
}

// [a,b)ã®æœ€å°å€¤ã‚’æ±‚ã‚ã‚‹
// å¾Œã‚ã®ã»ã†ã®å¼•æ•°ã¯ã€è¨ˆç®—ã®ç°¡å˜ã®ãŸã‚ã®å¼•æ•°ã€‚
// kã¯ç¯€ç‚¹ã®ç•ªå·ã€l, rã¯ãã®ç¯€ç‚¹ãŒ[l, r)ã«å¯¾å¿œã¥ã„ã¦ã„ã‚‹ã“ã¨ã‚’è¡¨ã™ã€‚
// ã—ãŸãŒã£ã¦ã€å¤–ã‹ã‚‰ã¯query(a, b, 0, 0, nn)ã¨ã—ã¦å‘¼ã¶ã€‚
ll query(int a, int b, int k, int l, int r, int m){
    // [a, b)ã¨[l, r)ãŒäº¤å·®ã—ãªã‘ã‚Œã°ã€INT_MAX
    if (r <= a || b <= l)return (ll)123456789123456789;

    // [a, b)ãŒ[l, r)ã‚’å®Œå…¨ã«å«ã‚“ã§ã„ã‚Œã°ã€ã“ã®ç¯€ç‚¹ã®å€¤
    if (a <= l && r <= b)return dat[k][m];
    else {
        // ãã†ã§ãªã‘ã‚Œã°ã€2ã¤ã®å­ã®æœ€å°å€¤
        ll vl = query(a, b, k * 2 + 1, l, (l + r) / 2, m);
        ll vr = query(a, b, k * 2 + 2, (l + r) / 2, r, m);
        return min(vl, vr);
    }
}


